import types from '../lib/core'
import { Model as csn, Definition } from "./csn"
import { Query as cqn } from "./cqn"
import { ReflectedModel, LinkedModel, LinkedDefinition } from './reflected'

export = cds
declare const cds : {

	/**
	 * Turns the given plain CSN model into a linked model
	 * @see [capire](https://cap.cloud.sap/docs/node.js/api#cds-reflect)
	 */
	linked (model : csn) : LinkedModel

	/**
	 * Turns the given plain CSN model into a reflected model
	 * @see [capire](https://cap.cloud.sap/docs/node.js/api#cds-reflect)
	 */
	reflect (model : csn) : ReflectedModel

	/** @see [capire](https://cap.cloud.sap/docs/node.js/api#cds-reflect) */
	reflect (def : Definition) : LinkedDefinition

	infer (query : cqn, model : csn) : LinkedDefinition

	/** Convenient shortcut to {@link this.builtin.classes.Association}  */
	Association : Definition

	/** Convenient shortcut to {@link this.builtin.classes.Composition}  */
	Composition : Definition

	/** Convenient shortcut to {@link this.builtin.classes.context}  */
	context : Definition

	/** Convenient shortcut to {@link this.builtin.classes.service}  */
	service : Definition

	/** Convenient shortcut to {@link this.builtin.classes.entity}  */
	entity : Definition

	/** Convenient shortcut to {@link this.builtin.classes.struct}  */
	struct : Definition

	/** Convenient shortcut to {@link this.builtin.classes.array}  */
	array : Definition

} & typeof import ('../lib/index')
