const path = require('path');

const BaseCommand = require('./base_command');
const clientLog = require('./helper/logging');

class Disconnect extends BaseCommand {

    static async run(folder, all) {

        if (!all) {
            if (!folder) {
                folder = '.';
            }
            await Disconnect.deleteSettings(path.resolve(folder));
        } else {
            await Disconnect.deleteAllSettings();
        }

    }

}

module.exports = Disconnect;
