class ClientLog {

    static log(logText) {
        console.log(logText);
    }

    static error(error, errorText) {
        if (!errorText) {
            errorText = error.toString();
        }
        console.error(errorText + '\n' + ( (process.env.DEBUG && error && error.stack) ? error.stack : ''));
    }

    static debug(error, debugText) {
        if (process.env.DEBUG) {
            console.log(debugText + '\n' + ( (error && error.stack) ? error.stack : '') );
        }
    };
}

module.exports = ClientLog;